def topLeftDiag(pattern, row, col):
        if (row - 3 >= 0 and row < len(pattern)) and (col - 3 >= 0 and col < len(pattern[0])):
                return pattern[row][col] + pattern[row - 1][col - 1] + pattern[row - 2][col - 2] + pattern[row - 3][col - 3] == 'XMAS'
        return False

def topRightDiag(pattern, row, col):
        if (row - 3 >= 0 and row < len(pattern)) and (col >= 0 and col + 3 < len(pattern[0])):
                return pattern[row][col] + pattern[row - 1][col + 1] + pattern[row - 2][col + 2] + pattern[row - 3][col + 3] == 'XMAS'
        return False

def bottomLeftDiag(pattern, row, col):
        if (row >= 0 and row + 3 < len(pattern)) and (col - 3 >= 0 and col < len(pattern[0])):
                return pattern[row][col] + pattern[row + 1][col - 1] + pattern[row + 2][col - 2] + pattern[row + 3][col - 3] == 'XMAS'
        return False

def bottomRightDiag(pattern, row, col):
        if (row >= 0 and row + 3 < len(pattern)) and (col >= 0 and col + 3 < len(pattern[0])):
                return pattern[row][col] + pattern[row + 1][col + 1] + pattern[row + 2][col + 2] + pattern[row + 3][col + 3] == 'XMAS'
        return False

def right(pattern, row, col):
        if (col >= 0 and col + 3 < len(pattern[0])):
                return pattern[row][col] + pattern[row][col + 1] + pattern[row][col + 2] + pattern[row][col + 3] == 'XMAS'
        return False

def left(pattern, row, col):
        if (col - 3 >= 0 and col < len(pattern[0])):
                return pattern[row][col] + pattern[row][col - 1] + pattern[row][col - 2] + pattern[row][col - 3] == 'XMAS'
        return False

def up(pattern, row, col):
        if (row - 3 >= 0 and row < len(pattern)):
                return pattern[row][col] + pattern[row - 1][col] + pattern[row - 2][col] + pattern[row - 3][col] == 'XMAS'
        return False

def down(pattern, row, col):
        if (row >= 0 and row + 3 < len(pattern)):
                return pattern[row][col] + pattern[row + 1][col] + pattern[row + 2][col] + pattern[row + 3][col] == 'XMAS'
        return False


def searchXMAS(pattern):
        xmasCount = 0
        for row in range(len(pattern)):
                for col in range(len(pattern[0])):
                        if topLeftDiag(pattern, row, col):
                                xmasCount += 1
                        if topRightDiag(pattern, row, col):
                                xmasCount += 1
                        if bottomLeftDiag(pattern, row, col):
                                xmasCount += 1
                        if bottomRightDiag(pattern, row, col):
                                xmasCount += 1
                        if left(pattern, row, col):
                                xmasCount += 1
                        if right(pattern, row, col):
                                xmasCount += 1
                        if up(pattern, row, col):
                                xmasCount += 1
                        if down(pattern, row, col):
                                xmasCount += 1
        return xmasCount


